mapper.sql:
where status != 0

	and app_key = #{appKey}


	and name like  concat('%',#{name},'%')


	and status = #{status}

mapper.select:
select * from app


    order by ${orderField} ${orderType}

limit #{offset},#{pageSize}

mapper.insert:
insert into app(name,en_name,app_key,app_secret,status,description,create_time,update_time)
values(#{name},#{enName},#{appKey},#{appSecret},#{status},#{description},#{createTime},#{updateTime})
ON DUPLICATE KEY update name=#{name},app_secret=#{appSecret},status=#{status},update_time=now(),description=#{description}

mapper.update:
		update app


				name = #{name},


                enName = #{enName},


				description = #{description}


				status = #{status},

			update_time = #{updateTime}

		 where app_key=#{appKey}

mapper.select:
		select * from app where app_key in

            #{appKey}

        and status != 0
