mapper.update:
delete from data_grant_record
where app_key=#{appKey} and model_id=#{modelId}
 and operation_id=#{operationId}
and grant_target_id=#{grantTargetId} and grant_target_type=#{grantTargetType} and tenant=#{tenant}

mapper.select:
select t1.* from data_grant_record t1, data_grant_record_detail t2 
where t1.id = t2.data_grant_record_id and t1.model_id=#{modelId} and t1.status=1
	and (t1.expire_time is null or t1.expire_time > now())
	 and t1.operation_id=#{operationId}
	and t2.property_id=#{propertyId}
	limit 1

mapper.sql:
where app_key=#{appKey} and model_id=#{modelId}
 and operation_id=#{operationId}
and grant_target_id in

	#{grantTargetId}

and grant_target_type=#{grantTargetType}

    and tenant='_t_common'


    and tenant in ('_t_common',#{tenant})


and (effective_time is null or effective_time <= now())

and (expire_time is null or expire_time >= now())
and status=1

sql.foreach:
	(
	select t1.id from data_grant_record t1, data_grant_record_detail t2
	where t1.app_key=#{appKey} and t1.model_id=#{modelId} 
	 and t1.operation_id=#{operationId}
	and t1.grant_target_id in

		#{grantTargetId}

	and t1.grant_target_type=#{grantTargetType}  and t1.status=1

    	and t1.tenant='_t_common'


	    and t1.tenant in ('_t_common',#{tenant})


	and (t1.effective_time is null or t1.effective_time <= now()) 

	and (t1.expire_time is null or t1.expire_time >= now())
	and t1.id = t2.data_grant_record_id and t2.property_id=#{propertyId} and t2.property_value in

		#{propertyValue}

	)

