mapper.select:
select * from data_property_access
where app_key=#{appKey} and status != 0 and model_id = #{modelId}
 and grant_target_id = #{grantTargetId}
 and grant_target_type = #{grantTargetType}

	and tenant='_t_common'


	and tenant in ('_t_common',#{tenant})

 and status = #{status} 

	order by ${orderField} ${orderType}

mapper.insert:
insert into data_property_access (app_key, model_id, property_id, grant_target_id, grant_target_type, tenant ,access_type, status, create_time, update_time ) values
(#{item.appKey},#{item.modelId}, #{item.propertyId}, #{item.grantTargetId}, #{item.grantTargetType}, #{item.tenant}, #{item.accessType},#{item.status}, #{item.createTime}, #{item.updateTime})
ON DUPLICATE KEY update status=values(status),access_type=values(accessType),update_time = values{updateTime}

mapper.update:
update data_property_access set status = #{status}, update_time = now()
where app_key=#{appKey} and grant_target_id = #{grantTargetId} and grant_target_type = #{grantTargetType} and tenant = #{tenant}
and model_id = #{modelId} 

	and property_id in

		#{id}
