mapper.select:
select * from menu where group_id=#{appKey} and status != 0 
and subgroup=#{subgroup}
order by idx asc

select * from menu where id in

    #{id}

and status != 0

select count(1) from ( select t4.* from user_role_ref t1, role t2, role_menu_ref t3, menu t4 where t1.user_id=#{userId} and t1.tenant=#{tenant} and t4.app_key=#{appKey} and t1.role_id=t2.id and t1.role_id=t3.role_id and t3.menu_id=t4.id and t1.status=1 and t2.status=1 and t3.status=1 and t4.status=1 and t4.id in
#{id}
limit 1 ) t0
