mapper.insert:
insert into menu_permission_ref (menu_id,permission_id, status, create_time, update_time) values
(#{item.menuId}, #{item.permissionId}, #{item.status},#{item.updateTime},#{item.createTime})
ON DUPLICATE KEY UPDATE update_time = now(),status = values{status}

mapper.update:
update menu_permission_ref set status = #{status},update_time = now()
where menu_id = #{menuId}

	and permission_id in

		#{id}

mapper.sql:
where t1.status != 0 and t2.status = 1 and t1.menu_id = #{menuId} 
and t1.permission_id=t2.id

	and t1.status = #{status},


	and t2.permission_type=#{permissionType}


    and t2.permission_key = #{permissionKey}

mapper.select:
select t2.* from menu_permission_ref t1,permission t2


	order by ${orderField} ${orderType}

limit #{offset},#{pageSize}
