mapper.select:
select * from permission where status != 0 and app_key=#{appKey}
and permission_key in

	#{permissionKey}

mapper.sql:
where status != 0 and app_key=#{appKey}

	and type=#{type}


	and permission_key=#{permissionKey}


	and name=#{name}


	and status = #{status}

mapper.select:
select * from permission 


    order by ${orderField} ${orderType}

limit #{offset}, #{pageSize}

mapper.insert:
insert into permission(permission_key,name,en_name,type,app_key,description,update_time,create_time,status) values
(#{item.permissionKey},#{item.name},#{item.type},#{item.appKey},#{item.description},#{item.updateTime},#{item.createTime},#{status})
ON DUPLICATE KEY update status=values{status},name=values{name},type=values{type}, description=values{description},update_time = values{updateTime}
