mapper.sql:
where status != 0 and app_key = #{appKey}

    and tenant='_t_c'


    and tenant in ('_t_c',#{tenant})


	and subgroup = #{subgroup}


	and name like concat('%', #{name}, '%')


	and pid = #{pid}

mapper.select:
select * from permission_group 


    order by ${orderField} ${orderType}

limit #{offset},#{pageSize}
