mapper.insert:
insert into permission_group (app_key,tenant,type,pid,
name,en_name, description, idx, status, create_time, update_time)
values (#{appKey}, #{tenant},#{type}, #{pid}, #{name},
#{description}, #{idx}, #{status}, #{createTime},#{updateTime}
)

mapper.sql:
where status != 0 and app_key = #{appKey}

    and tenant='_t_all'


    and tenant in ('_t_all',#{tenant})


	and type = #{type}


	and name like concat('%', #{name}, '%')


	and pid = #{pid}

mapper.select:
select * from permission_group 


    order by ${orderField} ${orderType}

limit #{offset},#{pageSize}
