mapper.sql:
 where app_key = #{appKey} and status != 0

    and tenant='_t_all'


    and tenant in ('_t_all',#{tenant})


	and type = #{type}


	and role_id = #{roleId}


	and role_key = #{roleKey}


	and name like  concat('%', #{name}, '%')


	and status = #{status}

mapper.select:
	select * from role 


       order by ${orderField} ${orderType}

	limit #{offset},#{pageSize}
