mapper.sql:
 where app_key = #{appKey} and status != 0

    and tenant='_t_c'


    and tenant in ('_t_c',#{tenant})


	and subgroup = #{subgroup}


	and role_id = #{roleId}


	and role_key = #{roleKey}



		and name like concat('%', #{name}, '%')


		and name = #{name}



	and status = #{status}

mapper.select:
	select * from role 


       order by ${orderField} ${orderType}

	limit #{offset},#{pageSize}
