mapper.insert:
insert into role_permission_group_ref (role_id,group_id, status, create_time, update_time) values
(#{item.roleId}, #{item.groupId}, #{item.status},#{item.updateTime},#{item.createTime})
ON DUPLICATE KEY UPDATE update_time = now(),status = values{status}

mapper.update:
update role_permission_group_ref set status =
#{status},update_time = now()
where role_id = #{roleId}

	and group_id in

		#{id}

mapper.select:
select t2.* from role_permission_group_ref t1,permission_gorup t2


	order by ${orderField} ${orderType}

limit #{offset},#{pageSize}
