mapper.update:
update user_permission_group_ref set status = #{status},gmt_modified = now() where user_id=#{userId}

	and tenant=#{tenant}


	and group_id in

		#{groupId}

mapper.sql:
where t1.user_id=#{userId} and t1.tenant=#{tenant} and t1.group_id=t2.id and and t2.app_key=#{appKey}
and t1.status = #{status},
and t2.tenant='_t_c'
and t2.tenant in ('_t_c',#{tenant})
and t2.subgroup = #{subgroup}
and t2.name like concat('%', #{groupName}, '%')
and t2.id = #{groupId}
and (t1.effective_time <= now() or t1.effective_time is null)
and (t1.expire_time >= now() or t1.expire_time is null) and t2.status=1 and t1.status!=0

mapper.select:
	select t1.*,t2.app_key,t2.tenant as group_tenant,t2.group_pid,
	t2.name as groupName,t2.description as groupDescription, t2.idx as groupIdx,t2.subgroup as subgroup
	from user_permission_group_ref t1, permission_group t2


       order by ${orderField} ${orderType}

	limit #{offset},#{pageSize}
