mapper.sql:
where status != 0

	and app_key = #{appKey}


	and app_name like  concat('%',#{appName},'%')


	and status = #{status}

mapper.select:
select * from ice_app


    order by ${orderField} ${orderType}

limit #{offset},#{pageSize}

mapper.insert:
insert into ice_app(app_name,app_key,app_secret,status,description,create_time,update_time)
values(#{appName},#{appKey},#{appSecret},#{status},#{description},#{createTime},#{updateTime})
ON DUPLICATE KEY update app_name=#{appName},app_secret=#{appSecret},status=#{status},update_time=now(),description=#{description}

mapper.update:
update ice_app


		app_name = #{appName},


		description = #{description}


		status = #{status},

	update_time = #{updateTime}

 where app_key=#{appKey}

mapper.select:
		select * from ice_app where app_key in

            #{appKey}

        and status != 0
