mapper.select:
select * from ice_data_model where status = 1 and id in
#{modelId}
ORDER BY id desc

mapper.sql:
where status != 0 

	and app_key = #{appKey}


	and code = #{code}


	and name like concat('%', #{name}, '%')


	and status = #{status}

mapper.select:
select * from ice_data_model 


    order by ${orderField} ${orderType}

limit #{offset},#{pageSize}

mapper.update:
update ice_data_model


		name = #{name},


		description = #{description},


		status = #{status},

	update_time = #{updateTime}

where id = #{id}
