mapper.insert:
insert into ice_role_permission_group_ref (role_id,group_id, status, create_time, update_time) values
(#{item.roleId}, #{item.groupId}, #{item.status},#{item.updateTime},#{item.createTime})
ON DUPLICATE KEY UPDATE update_time = now(),status = values{status}

mapper.update:
update ice_role_permission_group_ref set status =
#{status},update_time = now()
where role_id = #{roleId}

	and group_id in

		#{id}

mapper.sql:
where t1.status != 0 and t2.status = 1 and t1.role_id = #{roleId} 
and t1.group_id=t2.id

	and t1.status = #{status},


	and t2.app_key=#{groupAppKey}


	and t2.group_type=#{groupType}


	and t2.group_name like concat('%', #{groupName}, '%')


	and t2.tenant='tenant_common_symbol'


	and t2.tenant in ('tenant_common_symbol',#{tenant})


	and t2.tag1=#{tag1}

mapper.select:
select t2.* from ice_role_permission_group_ref t1,ice_permission_gorup t2


	order by ${orderField} ${orderType}

limit #{offset},#{pageSize}

select t2.* from ice_role_permission_group_ref t1,ice_permission_group t2
where t1.group_id = t2.id and t1.`status` != 0 and t2.`status` = 1

	and t2.app_key=#{appKey}

and t1.role_id in

	#{roleId}
