mapper.update:
update ice_user_permission_group_ref set status = #{status},update_time = now() where user_id=#{userId}

	and tenant=#{tenant}


	and group_id in

		#{groupId}

mapper.sql:
		where t1.user_id=#{userId} and t1.tenant=#{tenant} and t1.group_id=t2.id 

            and t1.status = #{status},


		    and t2.app_key=#{appKey}


		    and t2.tenant='tenant_common_symbol'


		    and t2.tenant in ('tenant_common_symbol',#{tenant})


		    and t2.tag1=#{tag1}


		    and t2.group_type = #{groupType}


		    and t2.group_name like  concat('%', #{groupName}, '%')


		    and t2.id=#{groupId}


			and	(t1.expire_time >= now() or t1.expire_time is null) 
			and (t1.effective_time <= now() or t1.effective_time is null)

		and t2.status=1 and t1.status!=0

mapper.select:
	select t1.*,t2.app_key,t2.tenant as group_tenant,t2.tag1,t2.group_pid,
	t2.group_name,t2.description, t2.group_order,t2.group_type
	from ice_user_permission_group_ref t1, ice_permission_group t2


       order by ${orderField} ${orderType}

	limit #{offset},#{pageSize}
