mapper.update:
update ice_user_permission_ref set status = #{status},update_time = now() where user_id=#{userId}

	and tenant=#{tenant}


	and permission_id in

		#{permissionId}

mapper.sql:
		where t1.user_id=#{userId} and t1.tenant=#{tenant} and t1.permission_id=t2.id 

            and t1.status = #{status},


		    and t2.app_key=#{appKey}


		    and t2.tenant='tenant_common_symbol'


		    and t2.tenant in ('tenant_common_symbol',#{tenant})


		    and t2.tag1=#{tag1}


		    and t2.permission_key = #{permissionKey}


		    and t2.permission_type = #{permissionType}


		    and t2.id=#{permissionId}


			and	(t1.expire_time >= now() or t1.expire_time is null) 
			and (t1.effective_time <= now() or t1.effective_time is null)

		and t2.status=1 and t1.status!=0

mapper.select:
	select t1.*,t2.app_key,t2.tenant as permission_tenant,t2.permission_key,
	t2.permission_type,t2.permission_name,t2.description,t2.tag1
	from ice_user_permission_ref t1, ice_permission t2


       order by ${orderField} ${orderType}

	limit #{offset},#{pageSize}
